import React, { useState } from 'react';
import { Link } from 'react-router-dom';
import './Catalog.css';

const productData = [
  { id: 1, name: 'HP LaserJet M141cw', description: 'High-quality printer with excellent print speed.', price: '$2415', image: '/print.png' },
  { id: 2, name: 'HP LaserJet M111cw', description: 'Compact and efficient for small offices.', price: '$2100', image: '/print2.png' },
  { id: 3, name: 'HP LaserJet M110w', description: 'Best value for home and office use.', price: '$1800', image: '/print3.png' },
];

export const Catalog = () => {
  const [items] = useState(productData);
  const [searchTerm, setSearchTerm] = useState('');

  const handleSearch = (e) => {
    setSearchTerm(e.target.value);
  };

  const filteredItems = items.filter(item =>
    item.name.toLowerCase().includes(searchTerm.toLowerCase()) ||
    item.description.toLowerCase().includes(searchTerm.toLowerCase())
  );

  return (
    <section className="catalog">
      <h2>Catalog</h2>
      <input
        type="text"
        placeholder="Search..."
        value={searchTerm}
        onChange={handleSearch}
      />
      <div className="filters">
        <label>
          Filter 1
          <input type="checkbox" />
        </label>
        <label>
          Filter 2
          <input type="checkbox" />
        </label>
        <label>
          Filter 3
          <input type="checkbox" />
        </label>
        <button>Apply</button>
      </div>
      <ul>
        {filteredItems.map(product => (
          <li key={product.id}>
            <img src={product.image} alt={product.name} />
            <h3>{product.name}</h3>
            <p>{product.description}</p>
            <p>{product.price}</p>
            <Link to={`/item/${product.id}`}>
              <button>View more</button>
            </Link>
          </li>
        ))}
      </ul>
    </section>
  );
};
