import React, { useContext, useState } from 'react';
import { Link } from 'react-router-dom';
import DataContext from './DataContext';
import './Catalog.css';

export const Catalog = () => {
  const { printers } = useContext(DataContext);
  const [searchTerm, setSearchTerm] = useState('');
  const [filters, setFilters] = useState({
    size: '',
    color: '',
    type: ''
  });

  const handleSearch = (e) => {
    setSearchTerm(e.target.value);
  };

  const handleFilterChange = (e) => {
    setFilters({
      ...filters,
      [e.target.name]: e.target.value
    });
  };

  const filteredItems = printers.filter(item =>
    item.name.toLowerCase().includes(searchTerm.trim().toLowerCase()) &&
    (filters.size ? item.size === filters.size : true) &&
    (filters.color ? item.color === filters.color : true) &&
    (filters.type ? item.type === filters.type : true)
  );

  return (
    <section className="catalog">
      <h2>Catalog</h2>
      <input
        type="text"
        placeholder="Search by name..."
        value={searchTerm}
        onChange={handleSearch}
      />
      <div className="filters">
        <label>
          Size
          <select name="size" value={filters.size} onChange={handleFilterChange}>
            <option value="">All</option>
            <option value="Large">Large</option>
            <option value="Medium">Medium</option>
            <option value="Small">Small</option>
          </select>
        </label>
        <label>
          Color
          <select name="color" value={filters.color} onChange={handleFilterChange}>
            <option value="">All</option>
            <option value="White">White</option>
            <option value="Black">Black</option>
          </select>
        </label>
        <label>
          Type
          <select name="type" value={filters.type} onChange={handleFilterChange}>
            <option value="">All</option>
            <option value="Laser">Laser</option>
            <option value="Inkjet">Inkjet</option>
          </select>
        </label>
      </div>
      <ul>
        {filteredItems.map(product => (
          <li key={product.id}>
            <img src={product.image} alt={product.name} />
            <h3>{product.name}</h3>
            <p>{product.description}</p>
            <p>{product.price}</p>
            <Link to={`/item/${product.id}`}>
              <button>View more</button>
            </Link>
          </li>
        ))}
      </ul>
    </section>
  );
};
