import React, { useState, useEffect } from 'react';
import { getPrinters } from '../../api';
import './ProductTile.css';

const ProductTiles = () => {
  const [printers, setPrinters] = useState([]);
  const [showMore, setShowMore] = useState(false);

  useEffect(() => {
    const fetchPrinters = async () => {
      try {
        const response = await getPrinters();
        setPrinters(response.data);
      } catch (error) {
        console.error('Error fetching printers:', error);
      }
    };

    fetchPrinters();
  }, []);

  return (
    <section className="product-tiles">
      {printers.slice(0, 3).map(printer => (
        <div key={printer.id} className="product-tile">
          <img className="printer-pr" src={`/images/${printer.image}`} alt={printer.name} />
          <h3>{printer.name}</h3>
          <p>{printer.description}</p>
          <p>Price: {printer.price}</p>
        </div>
      ))}
      {!showMore && <button onClick={() => setShowMore(true)}>View more</button>}

      {showMore && printers.slice(3).map(printer => (
        <div key={printer.id} className="product-tile">
          <img className="printer-pr" src={`/images/${printer.image}`} alt={printer.name} />
          <h3>{printer.name}</h3>
          <p>{printer.description}</p>
          <p>Price: {printer.price}</p>
        </div>
      ))}
    </section>
  );
};

export default ProductTiles;
