const express = require('express');
const cors = require('cors');
const bodyParser = require('body-parser');

const app = express();
const PORT = process.env.PORT || 5000;

app.use(cors());
app.use(bodyParser.json());

const printers = [
    { id: 1, name: 'HP LaserJet M141cw', description: 'Better version of M111cw, new features.', price: '$415', image: '/print.png', size: 'Large', color: 'White', type: 'Laser' },
    { id: 2, name: 'HP LaserJet M111cw', description: 'Good choice for average user', price: '$500', image: '/print2.png', size: 'Medium', color: 'White', type: 'Laser' },
    { id: 3, name: 'HP InkJet M110w', description: 'Best value for home and office use.', price: '$400', image: '/print3.png', size: 'Small', color: 'Black', type: 'Inkjet' }
];

app.get('/api/printers', (req, res) => {
    const { size, color, type } = req.query;
    let filteredPrinters = printers;

    if (size) {
        filteredPrinters = filteredPrinters.filter(printer => printer.size === size);
    }

    if (color) {
        filteredPrinters = filteredPrinters.filter(printer => printer.color === color);
    }

    if (type) {
        filteredPrinters = filteredPrinters.filter(printer => printer.type === type);
    }

    console.log('Filtered Printers:', filteredPrinters);
    res.json(filteredPrinters);
});

app.get('/api/printers/:id', (req, res) => {
    const { id } = req.params;
    console.log(`Received request for printer with id: ${id}`);
    const printer = printers.find(p => p.id === parseInt(id));

    if (printer) {
        console.log('Printer found:', printer);
        res.json(printer);
    } else {
        console.log('Printer not found');
        res.status(404).send('Item not found');
    }
});

app.listen(PORT, () => {
    console.log(`Server is running on port ${PORT}`);
});
