import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';
import './CartPage.css';

export const CartPage = () => {
  const [cart, setCart] = useState([]);
  const navigate = useNavigate();

  useEffect(() => {
    axios.get('http://localhost:5000/api/cart')
      .then(response => {
        setCart(response.data);
      })
      .catch(error => {
        console.error('Error loading cart data', error);
      });
  }, []);

  const handleRemoveFromCart = (id) => {
    axios.delete(`http://localhost:5000/api/cart/${id}`)
      .then(() => {
        setCart(cart.filter(item => item.id !== id));
      })
      .catch(error => {
        console.error('Error removing item from cart', error);
      });
  };

  const calculateTotalPrice = () => {
    return cart.reduce((total, item) => total + item.totalPrice, 0);
  };

  const handleBackToItems = () => {
    navigate(-1); 
  };

  return (
    <div className="cart-page">
      <h1>Shopping Cart</h1>
      {cart.length > 0 ? (
        <ul>
          {cart.map((item) => (
            <li key={item.id}>
              <img src={item.image} alt={item.name} className="cart-item-image" />
              <div>
                <p>{item.name}</p>
                <p>Quantity: {item.quantity} шт.</p>
                <p>Price: ${item.price}</p>
                <p>Сума: ${item.price * item.quantity}</p>
                <p>Guarantee: {item.guarantee}</p>
                <button onClick={() => handleRemoveFromCart(item.id)}>Remove</button>
              </div>
            </li>
          ))}
        </ul>
      ) : (
        <p class="p">Ваша корзина порожня (купи шось)</p>
      )}
      {cart.length > 0 && <h2>Загальна сума: ${calculateTotalPrice()}</h2>}
      <button onClick={handleBackToItems}>Back to Items</button>
    </div>
  );
};
