import React from 'react';
import { useSelector, useDispatch } from 'react-redux';
import { removeFromCart } from '../../actions';
import './CartPage.css';

export const CartPage = () => {
  const cart = useSelector((state) => state.cart);
  const dispatch = useDispatch();

  const handleRemoveFromCart = (id) => {
    dispatch(removeFromCart(id));
  };

  const calculateTotalPrice = () => {
    return cart.reduce((total, item) => {
      const price = parseFloat(item.price) || 0; // Приводимо до числа або 0
      const quantity = parseInt(item.quantity, 10) || 0; // Приводимо до числа або 0
      return total + price * quantity;
    }, 0);
  };

  return (
    <div className="cart-page">
      <h1>Shopping Cart</h1>
      {cart.length > 0 ? (
        <ul>
          {cart.map((item) => (
            <li key={item.id}>
              <img src={item.image} alt={item.name} className="cart-item-image" />
              <div>
                <p>{item.name}</p>
                <p>Quantity: {item.quantity} шт.</p>
                <p>Price: {item.price * item.quantity}</p> {/* Загальна ціна для цього товару */}
                <p>Guarantee: {item.guarantee}</p>
                <button onClick={() => handleRemoveFromCart(item.id)}>Видалити</button>
              </div>
            </li>
          ))}
        </ul>
      ) : (
        <p className="p">ВАША КОРЗИНА ПОРОЖНЯ (ми чекаєм)</p>
      )}
      {cart.length > 0 && <h2>Загальна сума: ${calculateTotalPrice()}</h2>} {/* Загальна сума */}
    </div>
  );
};
