import React, { useState } from 'react';
import { Link } from 'react-router-dom';
import { Select } from './Select';
import './Catalog.css';

const productData = [
  { id: 1, name: 'Printer Model 1', description: 'High-quality printer with excellent print speed.', price: '$2415' },
  { id: 2, name: 'Printer Model 2', description: 'Compact and efficient for small offices.', price: '$2100' },
  { id: 3, name: 'Printer Model 3', description: 'Best value for home and office use.', price: '$1800' },
];

export const Catalog = () => {
  const [items, setItems] = useState(productData);
  const [searchTerm, setSearchTerm] = useState('');

  const handleSearch = (e) => {
    setSearchTerm(e.target.value);
  };

  const filteredItems = items.filter(item =>
    item.name.toLowerCase().includes(searchTerm.toLowerCase()) ||
    item.description.toLowerCase().includes(searchTerm.toLowerCase())
  );

  return (
    <section className="catalog">
      <h2>Catalog</h2>
      <input 
        type="text" 
        placeholder="Search..."
        value={searchTerm}
        onChange={handleSearch}
      />
      <ul>
        {filteredItems.map(product => (
          <li key={product.id}>
            <h3>{product.name}</h3>
            <p>{product.description}</p>
            <p>{product.price}</p>
            <Link to={`/item/${product.id}`}>
              <button>View more</button>
            </Link>
          </li>
        ))}
      </ul>
    </section>
  );
};
