import React, { useState, useEffect } from 'react';
import { getPrinters } from '../../api';

const Hero = () => {
  const [printers, setPrinters] = useState([]);

  useEffect(() => {
    const fetchPrinters = async () => {
      try {
        const response = await getPrinters();
        setPrinters(response.data);
      } catch (error) {
        console.error('Error fetching printers:', error);
      }
    };

    fetchPrinters();
  }, []);

  return (
    <section className="hero">
      <img className="printer-hero" src="/printerlo.jpg" alt="Featured Printer" />
      <h2>Top Printer Deals</h2>
      <p>
        Explore our range of printers that combine functionality, quality, and performance. Whether you're looking for a high-speed office solution, a versatile home printer, 
        or an affordable option for students, we've got something for everyone. Our printers are designed to meet your needs and exceed
      </p>
      {printers.slice(0, 3).map(printer => (
        <div key={printer.id}>
          <h3>{printer.name}</h3>
          <p>{printer.description}</p>
          <p>Price: {printer.price}</p>
        </div>
      ))}
    </section>
  );
};

export default Hero;
