import React from 'react';
import './components.css';

const extraContent = [
  { id: 1, title: 'Additional Information 1', text: 'This is some extra content for your home page.' },
  { id: 2, title: 'Additional Information 2', text: 'Here is some more extra content for your home page.' }
];

export const Hero = () => {
  const [content, setContent] = useState([]);

  const handleViewMore = () => {
    setContent(extraContent);
  };
  return (
    <section className="hero">
      <img class="printer-hero" src="/printerlo.jpg" alt="Featured Printer" />
      <h2>Top Printer Deals</h2>
      <p>
      Explore our range of printers that combine functionality, quality, and performance. Whether you're looking for a high-speed office solution, a versatile home printer, 
      or an affordable option for students, we've got something for everyone. Our printers are designed to meet your needs and exceed 
      </p>
      <button onClick={handleViewMore}>View more</button>
      {content.map(item => (
        <div key={item.id}>
          <h3>{item.title}</h3>
          <p>{item.text}</p>
        </div>
      ))}
    </section>
  );
};
