import React, { useEffect } from 'react';
import { useSelector, useDispatch } from 'react-redux';
import { fetchCart, removeFromCartAPI } from '../../actions/cartActions';
import { useNavigate } from 'react-router-dom';
import './CartPage.css';

export const CartPage = () => {
  const cart = useSelector((state) => state.cart);
  const dispatch = useDispatch();
  const navigate = useNavigate();

  useEffect(() => {
    dispatch(fetchCart()); // Завантаження корзини при першому рендері
  }, [dispatch]);

  const handleRemoveFromCart = (id) => {
    dispatch(removeFromCartAPI(id));
  };

  const calculateTotalPrice = () => {
    return cart.reduce((total, item) => total + item.price * item.quantity, 0);
  };

  const handleBackToItems = () => {
    navigate(-1);
  };

  return (
    <div className="cart-page">
      <h1>Shopping Cart</h1>
      {cart.length > 0 ? (
        <ul>
          {cart.map((item) => (
            <li key={item.id}>
              <img src={item.image} alt={item.name} className="cart-item-image" />
              <div>
                <p>{item.name}</p>
                <p>Quantity: {item.quantity} шт.</p>
                <p>Price: ${item.price}</p>
                <p>Сума: ${item.price * item.quantity}</p>
                <p>Guarantee: {item.guarantee}</p>
                <button onClick={() => handleRemoveFromCart(item.id)}>Remove</button>
              </div>
            </li>
          ))}
        </ul>
      ) : (
        <p className="p">Ваша корзина порожня (купи щось)</p>
      )}
      {cart.length > 0 && <h2>Загальна сума: ${calculateTotalPrice()}</h2>}
      <button onClick={handleBackToItems}>Back to Items</button>
    </div>
  );
};
