import React, { useState, useEffect } from 'react';
import { useParams } from 'react-router-dom';
import axios from 'axios';
import './ItemPage.css';

const BASE_URL = 'http://localhost:5000/api';

export const ItemPage = () => {
  const { id } = useParams(); // Отримання ID продукту з URL
  const [product, setProduct] = useState(null); // Дані продукту
  const [loading, setLoading] = useState(true); // Стан завантаження
  const [error, setError] = useState(null); // Стан помилки

  useEffect(() => {
    const fetchProduct = async () => {
      try {
        const response = await axios.get(`${BASE_URL}/printers/${id}`);
        setProduct(response.data);
        setLoading(false);
      } catch (err) {
        console.error('Помилка при завантаженні продукту:', err);
        setError('Продукт не знайдено');
        setLoading(false);
      }
    };

    fetchProduct();
  }, [id]);

  if (loading) {
    return <h2>Завантаження...</h2>;
  }

  if (error) {
    return <h2>{error}</h2>;
  }

  return (
    <div className="item-page">
      <h2>{product.name}</h2>
      <img src={product.image} alt={product.name} />
      <p>{product.description}</p>
      <p className="details">{product.size} / {product.type}</p>
      <p>Ціна: {product.price}</p>
      <div className="quantity-field">
        <label>
          Кількість:
          <input
            type="number"
            min="0"
            defaultValue="1"
          />
        </label>
      </div>
      <button>Додати в кошик</button>
    </div>
  );
};
