import React, { useState, useEffect } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import './ItemPage.css';

const productData = [
  { id: 1, name: 'HP LaserJet M141cw', description: 'Better version of M111cw, new features.', price: '$2415', image: '/print.png', countableField: 1337, selectableField: 'Select', branding: 'Lorem ipsum dolor sit amet, consectetur adipiscing elit. Nunc maximus, nulla ut commodo' },
  { id: 2, name: 'HP LaserJet M111cw', description: 'Good choice for average user', price: '$2100', image: '/print2.png', countableField: 1234, selectableField: 'Select', branding: 'Lorem ipsum dolor sit amet, consectetur adipiscing elit. Nunc maximus, nulla ut commodo' },
  { id: 3, name: 'HP InkJet M110w', description: 'Best value for home and office use.', price: '$1800', image: '/print3.png', countableField: 5678, selectableField: 'Select', branding: 'Lorem ipsum dolor sit amet, consectetur adipiscing elit. Nunc maximus, nulla ut commodo' },
];

export const ItemPage = () => {
  const { id } = useParams();
  const navigate = useNavigate();
  const [product, setProduct] = useState(null);

  useEffect(() => {
    const foundProduct = productData.find(product => product.id === parseInt(id));
    setProduct(foundProduct);
  }, [id]);

  if (!product) {
    return <h2>Item not found</h2>;
  }

  return (
    <div className="item-page">
      <h2>{product.name}</h2>
      <img src={product.image} alt={product.name} />
      <p>{product.description}</p>
      <p>Countable field: {product.countableField}</p>
      <p>Selectable field: {product.selectableField}</p>
      <p>Price: {product.price}</p>
      <button onClick={() => navigate(-1)}>Go back</button>
      <button>Add to cart</button>
      <div className="branding">
        <p>Branding stuff: {product.branding}</p>
      </div>
    </div>
  );
};
