import React, { useState, useEffect } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import axios from 'axios';
import './ItemPage.css';

const BASE_URL = 'http://localhost:5000/api';

export const ItemPage = () => {
  const { id } = useParams();
  const navigate = useNavigate();
  const [product, setProduct] = useState(null);
  const [quantity, setQuantity] = useState(1);

  useEffect(() => {
    const fetchProduct = async () => {
      try {
        console.log(`Fetching product with id: ${id}`);
        const response = await axios.get(`${BASE_URL}/printers/${id}`);
        console.log('Product response:', response.data);
        setProduct(response.data);
      } catch (error) {
        console.error('Error fetching product:', error);
        navigate('/not-found'); // Перехід на сторінку 404
      }
    };

    if (id) {
      fetchProduct();
    }
  }, [id, navigate]);

  if (!product) {
    return <h2>Loading...</h2>; // Показуємо "Завантаження", поки дані не будуть отримані
  }

  const handleQuantityChange = (e) => {
    const value = Math.max(0, e.target.value === '' ? 0 : parseInt(e.target.value));
    setQuantity(value);
  };

  return (
    <div className="item-page">
      <h2>{product.name}</h2>
      <img src={product.image} alt={product.name} />
      <p>{product.description}</p>
      <p className="details">{product.size} / {product.type}</p>
      <p>Price: {product.price}</p>
      <div className="quantity-field">
        <label>
          Quantity:
          <input
            type="number"
            value={quantity}
            onChange={handleQuantityChange}
            min="0"
          />
        </label>
      </div>
      <div className="selectable-field">
        <label>
          Guarantee:
          <select>
            <option value="12 months">12 months</option>
            <option value="24 months">24 months</option>
          </select>
        </label>
      </div>
      <button onClick={() => navigate(-1)}>Go back</button>
      <button>Add to cart</button>
    </div>
  );
};
