import React, { useState, useEffect } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import './ItemPage.css';

const productData = [
  { id: 1, name: 'HP LaserJet M141cw', description: 'Better version of M111cw, new features.', price: '$2415', image: '/print.png', size: 'Large', color: 'White', type: 'Laser' },
  { id: 2, name: 'HP LaserJet M111cw', description: 'Good choice for average user', price: '$2100', image: '/print2.png', size: 'Medium', color: 'White', type: 'Laser' },
  { id: 3, name: 'HP InkJet M110w', description: 'Best value for home and office use.', price: '$1800', image: '/print3.png', size: 'Small', color: 'Black', type: 'Inkjet' },
];

export const ItemPage = () => {
  const { id } = useParams();
  const navigate = useNavigate();
  const [product, setProduct] = useState(null);
  const [count, setCount] = useState(0);

  useEffect(() => {
    const foundProduct = productData.find(product => product.id === parseInt(id));
    setProduct(foundProduct);
    if (foundProduct) setCount(foundProduct.countableField);
  }, [id]);

  if (!product) {
    return <h2>Item not found</h2>;
  }

  const handleIncrement = () => {
    setCount(count + 1);
  };

  return (
    <div className="item-page">
      <h2>{product.name}</h2>
      <img src={product.image} alt={product.name} />
      <p>{product.description}</p>
      <p className="details">{product.size} / {product.type}</p>
      <p>Price: {product.price}</p>
      <div className="countable-field">
        <button onClick={handleIncrement}>Countable field: {count}</button>
      </div>
      <div className="selectable-field">
        <label>
          Selectable field:  
          <select>
            <option value="select">Select</option>
          </select>
        </label>
      </div>
      <button onClick={() => navigate(-1)}>Go back</button>
      <button>Add to cart</button>
    </div>
  );
};
