import axios from 'axios';

export const ADD_TO_CART = 'ADD_TO_CART';
export const REMOVE_FROM_CART = 'REMOVE_FROM_CART';
export const SET_CART = 'SET_CART';

// Додати товар у локальний стан
export const addToCart = (product) => ({
  type: ADD_TO_CART,
  payload: product,
});

// Видалити товар із локального стану
export const removeFromCart = (productId) => ({
  type: REMOVE_FROM_CART,
  payload: productId,
});

// Синхронізувати стан корзини з API
export const setCart = (cartItems) => ({
  type: SET_CART,
  payload: cartItems,
});

// Завантажити корзину з API
export const fetchCart = () => async (dispatch) => {
  try {
    const response = await axios.get('http://localhost:5000/api/cart');
    dispatch(setCart(response.data));
  } catch (error) {
    console.error('Помилка завантаження корзини:', error);
  }
};

// Додати товар у корзину через API
export const addToCartAPI = (product) => async (dispatch) => {
  try {
    await axios.post('http://localhost:5000/api/cart', product);
    dispatch(addToCart(product));
  } catch (error) {
    console.error('Помилка додавання товару в корзину:', error);
  }
};

// Видалити товар із корзини через API
export const removeFromCartAPI = (productId) => async (dispatch) => {
  try {
    await axios.delete(`http://localhost:5000/api/cart/${productId}`);
    dispatch(removeFromCart(productId));
  } catch (error) {
    console.error('Помилка видалення товару з корзини:', error);
  }
};
