import React, { useEffect, useState } from 'react';
import { useSelector, useDispatch } from 'react-redux';
import { removeFromCart } from '../../actions';
import { useNavigate } from 'react-router-dom';
import axios from 'axios';
import './CartPage.css';

export const CartPage = () => {
  const cartFromStore = useSelector((state) => state.cart); // Дані з Redux
  const dispatch = useDispatch();
  const navigate = useNavigate();

  const [cart, setCart] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  // Завантаження даних з сервера
  useEffect(() => {
    const fetchCart = async () => {
      try {
        const response = await axios.get('http://localhost:5000/api/cart');
        setCart(response.data); // Зберігаємо товари в локальний state
      } catch (err) {
        setError(err.message);
      } finally {
        setLoading(false);
      }
    };

    fetchCart();
  }, []);

  // Видалення товару
  const handleRemoveFromCart = async (id) => {
    try {
      await axios.delete(`http://localhost:5000/api/cart/${id}`);
      setCart((prevCart) => prevCart.filter((item) => item.id !== id)); // Оновлюємо state після видалення
      dispatch(removeFromCart(id)); // Оновлюємо Redux-стан
    } catch (err) {
      setError('Failed to remove item from cart');
    }
  };

  const calculateTotalPrice = () => {
    return cart.reduce((total, item) => total + item.price * item.quantity, 0);
  };

  const handleBackToItems = () => {
    navigate(-1);
  };

  if (loading) {
    return <p>Loading...</p>;
  }

  if (error) {
    return <p>Error: {error}</p>;
  }

  return (
    <div className="cart-page">
      <h1>Shopping Cart</h1>
      {cart.length > 0 ? (
        <ul>
          {cart.map((item) => (
            <li key={item.id}>
              <img src={item.image} alt={item.name} className="cart-item-image" />
              <div>
                <p>{item.name}</p>
                <p>Quantity: {item.quantity} шт.</p>
                <p>Price: ${item.price}</p>
                <p>Сума: ${item.price * item.quantity}</p>
                <p>Guarantee: {item.guarantee}</p>
                <button onClick={() => handleRemoveFromCart(item.id)}>Remove</button>
              </div>
            </li>
          ))}
        </ul>
      ) : (
        <p className="p">Ваша корзина порожня (купи щось)</p>
      )}
      {cart.length > 0 && <h2>Загальна сума: ${calculateTotalPrice()}</h2>}
      <button onClick={handleBackToItems}>Back to Items</button>
    </div>
  );
};
