import React, { useContext, useState, useEffect } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import DataContext from './DataContext';
import './ItemPage.css';

export const ItemPage = () => {
  const { id } = useParams();
  const navigate = useNavigate();
  const { printers } = useContext(DataContext);
  const [product, setProduct] = useState(null);
  const [count, setCount] = useState(0);

  useEffect(() => {
    const foundProduct = printers.find(product => product.id === parseInt(id));
    setProduct(foundProduct);
    if (foundProduct) setCount(foundProduct.countableField || 0); 
  }, [id, printers]);

  if (!product) {
    return <h2>Item not found</h2>;
  }

  const handleIncrement = () => {
    setCount(count + 1);
  };

  return (
    <div className="item-page">
      <h2>{product.name}</h2>
      <img src={product.image} alt={product.name} />
      <p>{product.description}</p>
      <p className="details">{product.size} / {product.type}</p>
      <p>Price: {product.price}</p>
      <div className="countable-field">
        <button onClick={handleIncrement}>Countable field: {count}</button>
      </div>
      <div className="selectable-field">
        <label>
          Selectable field:  
          <select>
            <option value="select">Select</option>
          </select>
        </label>
      </div>
      <button onClick={() => navigate(-1)}>Go back</button>
      <button>Add to cart</button>
    </div>
  );
};
