import React from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import './ItemPage.css';

const productData = [
  { id: 1, name: 'HP LaserJet M110w', description: 'High-quality printer with excellent print speed.', price: '$2415', image: '/print.png' },
  { id: 2, name: 'Printer Model 2', description: 'Compact and efficient for small offices.', price: '$2100', image: '/print2.png' },
  { id: 3, name: 'Printer Model 3', description: 'Best value for home and office use.', price: '$1800', image: '/print3.png' },
];

export const ItemPage = () => {
  const { id } = useParams();
  const navigate = useNavigate();
  const product = productData.find(product => product.id === parseInt(id));

  if (!product) {
    return <h2>Item not found</h2>;
  }

  return (
    <div className="item-page">
      <h2>{product.name}</h2>
      <img src={product.image} alt={product.name} />
      <p>{product.description}</p>
      <p>{product.price}</p>
      <button onClick={() => navigate(-1)}>Go back</button>
      <button>Add to cart</button>
    </div>
  );
};
